const Header = () => {
  const [showSideNav, setShowSideNav] = useState(false);

  const toggleSideNav = () => {
    setShowSideNav(!showSideNav);
  };

  return (
    <div className="header">
      <div className="menu-button" onClick={toggleSideNav}>
        <div></div>
        <div></div>
        <div></div>
      </div>

      <div className="logo">
        <Link to={DASHBOARD}>
          <img src={ajollaLogo} alt="logo" />
        </Link>
      </div>

      <div className="header-options">
        <div className="search">
          <input type="text" name="search" placeholder="Type here...." />
          <img src={searchIcon} alt="icon" className="search-icon" />
        </div>

        <span className="settings-icon">{settingsIcon}</span>
        {/* <img src={settingIcon} alt="icon" /> */}
        <img src={rotate} alt="icon" className="reload-icon" />
      </div>

      {/* SIDE NAV DRAWER */}
      <SideNav toggleSideNav={toggleSideNav} showSideNav={showSideNav} />
    </div>
  );
};

export default Header;
